INSERT INTO `sys_api` VALUES ('121', '0000000002', '49', '四新及异常品人工拉号产品列表', 'fff29f5a04', 'SELECT a.cart_number 车号,a.gz_num 冠字,a.proc_plan 计划执行工艺,a.proc_real 实际执行工艺,CONVERT (VARCHAR,a.rec_time,120) 记录时间,a.check_type 验证类型,(  CASE  WHEN a.manual_check_status = 0 THEN  \'未领取\'  ELSE  \'已领取\'  END ) 领取状态,(  CASE  WHEN a.complete_status = 0 THEN  \'未完成\'  ELSE  \'已完成\'  END ) 检封完工状态,a.process_name 当前工序, isnull(b.reason,\'\')+isnull((case when alpha_num is null then c.machine_name+c.proc_name+\'四新验证,\'+c.reason else c.alpha_num+cast(c.num1 as varchar)+\'至\'+cast(c.num2 as VARCHAR)+\'冠字验证\' end),\'\') 备注 FROM  print_wms_proclist a left join Print_Abnormal_Prod b on a.check_type=\'异常品处理\' and a.task_id=b.id left join print_newproc_plan c on a.check_type=\'四新验证\' and a.task_id=c.id WHERE a.manual_check_status =0 and proc_plan LIKE \'%人工拉号%\' AND CONVERT (VARCHAR,a.rec_time,112) BETWEEN 20180501 AND 20180533 ORDER BY (  CASE  WHEN a.check_type = \'异常品处理\' THEN  0  ELSE  1  END ),3,manual_check_status,2', 'tstart,tend', '', '2018-05-15 14:40:26', '2018-05-17 10:10:18');
