INSERT INTO `sys_api` VALUES ('129', '0000000002', '49', '任务处理系统服务运行状态', '7d948b095a', 'SELECT a.task_name,convert(varchar,rec_time,120) rec_time,datediff(SECOND,rec_time,getdate()) date_diff,(case when datediff(SECOND,rec_time,getdate())<100 then \\\'存活\\\' else \\\'异常\\\' end) live_status FROM print_wms_heartbeat a', '', '', '2018-05-18 15:27:06', '2018-05-18 15:27:06');
