INSERT INTO `sys_api` VALUES ('131', '0000000015', '49', '物料账龄报表', '4172bb514d', 'SELECT A .*,(  CASE  WHEN A .结存数量 = 0 THEN  \'无\'  ELSE  TO_CHAR (   MAX (b.\"创建日期\"),  \'yyyy-mm-dd\'  )  END ) 帐龄 FROM (  SELECT  查询时间, 物料编码, 物料名称, MAX (收入数量) 末次收入数量, MAX (收入金额) 末次收入金额, MAX (来源情况) 来源情况, MIN (付出数量) 末次发出数量, MIN (付出金额) 末次发出金额, MAX (领用说明) 领用说明, 结存数量, 结存金额, 结存位置  FROM  (   SELECT   A .\"查询时间\",  A .\"物料编码\",  A .\"物料名称\",  (    CASE    WHEN 收入状态 = 1 THEN    A .收入数量    ELSE    0    END   ) 收入数量,  (    CASE    WHEN 收入状态 = 1 THEN    A .收入金额    ELSE    0    END   ) 收入金额,  (    CASE    WHEN 收入状态 = 1 THEN    A .来源情况    ELSE    \' \'    END   ) 来源情况,  (    CASE    WHEN 收入状态 = 0 THEN    A .收入数量    ELSE    0    END   ) 付出数量,  (    CASE    WHEN 收入状态 = 0 THEN    A .收入金额    ELSE    0    END   ) 付出金额,  (    CASE    WHEN 收入状态 = 0 THEN    A .来源情况    ELSE    \' \'    END   ) 领用说明,  b.结存数量,  b.结存金额,  b.结存位置   FROM   VIEW_CBPC_WMS_YEAR A,  view_cbpc_wms_year_count b   WHERE   A .物料编码 = \'4103.029105\'   AND (   A .TRANSACTION_ID IN (    SELECT    MAX (A .TRANSACTION_ID)    FROM    VIEW_CBPC_WMS_YEAR A    WHERE    A .物料编码 = \'4103.029105\'    AND 收入状态 = 1   )   OR A .TRANSACTION_ID IN (    SELECT    MAX (A .TRANSACTION_ID)    FROM    VIEW_CBPC_WMS_YEAR A    WHERE    A .物料编码 = \'4103.029105\'    AND 收入状态 = 0   )   )   AND b.物料编码 = \'4103.029105\'  ) A  GROUP BY  查询时间, 物料编码, 物料名称, 结存数量, 结存金额, 结存位置 ) A,(  SELECT  A .TRANSACTION_ID, A .\"物料编码\", A .\"物料名称\", A .创建日期, SUM (A .\"收入数量\") OVER (   PARTITION BY A .\"物料编码\"   ORDER BY   A .TRANSACTION_ID DESC  ) 累计库存  FROM  VIEW_CBPC_WMS_YEAR A  WHERE  A .物料编码 = \'4103.029105\'  AND A .收入状态 = 1 ) b WHERE A .结存数量 < b.\"累计库存\" GROUP BY A .查询时间,A .物料编码,A .物料名称,A .末次收入数量,A .末次收入金额,A .来源情况,A .末次发出数量,A .末次发出金额,A .领用说明,A .结存数量,A .结存金额,A .结存位置', '', '', '2018-05-18 17:16:36', '2018-05-18 18:08:48');
INSERT INTO `sys_api` VALUES ('132', '0000000013', '49', '查询批次状态', '6ac1e30d85', 'select org.strorgname 库房,def.strproductname 品种,st.strcarno 车号,ps.strpsname 工序,st.intstock 数量,(case when nvl2(bl.BATCH_NO,1,0) = 1 then \'锁定\' else \'未锁车\' end) 锁车状态,bl.REASON_DESC 锁车原因,nvl(tech.TECHNOLOGY_TYPE_NAME,\'不分工艺\') 工艺 from mac2.tbstock st left join mac2.vw_wim_tb_black_list bl on st.strcarno = bl.BATCH_NO left join (select nvl(t1.car_no,t2.car_no) car_no,nvl(t1.technology_name,t2.TECHNOLOGY_TYPE_NAME) TECHNOLOGY_TYPE_NAME from (select P.CAR_NO,P.TECHNOLOGY_TYPE_NAME from vw_dbjt_tech p where p.car_no in (?) group by p.car_no,p.technology_type_name) T2 full join (select t1.car_no,t1.technology_name from TBBASE_CAR_TECHNOLOGY_HISTORY t1 WHERE t1.STATUS = 1 and t1.car_no in (?)) t1 on 1=0) tech on st.strcarno = tech.CAR_NO left join mac2.tborg org on st.intorgid = org.intorgid left join mac2.tbproductdef def on def.intproductid = st.intproductid left join mac2.tbproductst ps on ps.strpscode = st.strpscode where st.strcarno in (?) and st.intstock > 0', 'carnos1,carnos2,carnos3', '', '2018-05-20 13:38:38', '2018-05-20 14:00:07');
INSERT INTO `sys_api` VALUES ('133', '0000000014', '49', '指定车号列表生产原始信息', '97b29df2a0', 'SELECT a.车号,   a.冠号,   a.字号,   a.工序,   a.班次,   a.机台,   a.机长,   to_char(a.开始时间,\\\'YYYY-MM-DD\\\') 生产日期,   to_char(a.开始时间,\\\'hh24:mi:ss\\\') 开始时间,   to_char(a.开始时间,\\\'day\\\') 星期,   (case     when to_char(a.开始时间,\\\'day\\\') = \\\'星期一\\\' then      1     when to_char(a.开始时间,\\\'day\\\') = \\\'星期二\\\' then      2     when to_char(a.开始时间,\\\'day\\\') = \\\'星期三\\\' then      3     when to_char(a.开始时间,\\\'day\\\') = \\\'星期四\\\' then      4     when to_char(a.开始时间,\\\'day\\\') = \\\'星期五\\\' then      5     when to_char(a.开始时间,\\\'day\\\') = \\\'星期六\\\' then      6     else      7    end) dayIdx,   a.品种  FROM CDYC_USER.VIEW_CARTFINDER a where substr(a.工序,0,1) in (\\\'胶\\\',\\\'凹\\\',\\\'印\\\',\\\'丝\\\')  and a.车号 in   (?)      order by 4,6,11,9', 'carts', '', '2018-05-21 10:42:05', '2018-05-21 10:42:05');
INSERT INTO `sys_api` VALUES ('134', '0000000002', '49', '产品锁车原因', 'dc12ffbbae', 'SELECT \'【异常品】\'+machine_name +\' \'+reason remark FROM Print_Abnormal_Prod a where cart_number = ? union ALL select distinct \'【人工大张日常抽检】\' remark from print_sample_cartlist where cart_number =? union all SELECT distinct \'【四新验证】\' remark FROM print_wms_proclist where cart_number = ? and check_type=\'四新验证\'', 'cart1,cart2,cart3', '', '2018-05-24 17:12:54', '2018-05-24 17:17:47');
